  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'network',
	title: 'Network'
};
export const metadata = {
  "id": "advanced/core/network",
  "title": "Network",
  "description": "## 当前目标",
  "source": "@site/docs/advanced/core/network.md",
  "permalink": "/new-docs/docs/advanced/core/network",
  "editUrl": "https://github.com/nervosnetwork/muta/edit/master/website/docs/advanced/core/network.md",
  "sidebar": "docs",
  "previous": {
    "title": "Overlord",
    "permalink": "/new-docs/docs/advanced/core/overlord"
  },
  "next": {
    "title": "Mempool",
    "permalink": "/new-docs/docs/advanced/core/mempool"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: '当前目标',
  id: '当前目标',
  children: []
}, {
  value: '消息收发',
  id: '消息收发',
  children: [{
    value: '节点消息端 (Endpoint)',
    id: '节点消息端-endpoint',
    children: []
  }, {
    value: '消息序列化',
    id: '消息序列化',
    children: []
  }, {
    value: '消息处理',
    id: '消息处理',
    children: []
  }, {
    value: '消息处理逻辑注册',
    id: '消息处理逻辑注册',
    children: []
  }, {
    value: '消息的发送',
    id: '消息的发送',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "当前目标"
    }}>{`当前目标`}</h2>
    <p>{`基于 `}<a parentName="p" {...{
        "href": "https://github.com/nervosnetwork/p2p"
      }}>{`tentacle crate`}</a>{` 实现一个简单的可工作的 P2P 网络，主要功能如下：`}</p>
    <ul>
      <li parentName="ul">
        <p parentName="li">{`节点身份`}</p>
        <ul parentName="li">
          <li parentName="ul">{`PeerID: secp256k1 的公钥派生出的 ID，`}<a parentName="li" {...{
              "href": "https://crates.io/crates/tentacle-secio"
            }}>{`tentacle-secio`}</a></li>
          <li parentName="ul">{`Address: MultiAddress `}<a parentName="li" {...{
              "href": "https://multiformats.io/multiaddr"
            }}>{`REF`}</a>{`，只支持 TCP`}</li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li">{`节点发现`}</p>
        <ul parentName="li">
          <li parentName="ul">{`bootstrap，`}<a parentName="li" {...{
              "href": "https://crates.io/crates/tentacle-discovery"
            }}>{`tentacle-discovery`}</a></li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li">{`节点质量维护`}</p>
        <ul parentName="li">
          <li parentName="ul">{`ping，`}<a parentName="li" {...{
              "href": "https://crates.io/crates/tentacle-ping"
            }}>{`tentacle-ping`}</a>{`，超时断开`}</li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li">{`节点持久化`}</p>
        <ul parentName="li">
          <li parentName="ul">{`基于文件的简易持久化，服务退出时，将保存节点信息，默认关闭`}</li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li">{`消息广播以及单播`}</p>
        <ul parentName="li">
          <li parentName="ul">{`基础的广播服务，以及基于 secp256k1 公钥地址的单播`}</li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li">{`消息加密传输`}</p>
        <ul parentName="li">
          <li parentName="ul">{`基于 `}<a parentName="li" {...{
              "href": "https://crates.io/crates/tentacle-secio"
            }}>{`tentacle-secio`}</a></li>
        </ul>
      </li>
      <li parentName="ul">
        <p parentName="li">{`其他`}</p>
        <ul parentName="li">
          <li parentName="ul">{`消息优先级: 使用 tentacle 自带的消息发送优先级，目前只有两种，High 和 Normal`}</li>
          <li parentName="ul">{`消息压缩: 使用 snappy`}</li>
          <li parentName="ul">{`消息处理: 基于 handler 注册形式，由各个模块自定义接受消息处理逻辑`}</li>
        </ul>
      </li>
    </ul>
    <h2 {...{
      "id": "消息收发"
    }}>{`消息收发`}</h2>
    <h3 {...{
      "id": "节点消息端-endpoint"
    }}>{`节点消息端 (Endpoint)`}</h3>
    <p>{`节点通过注册消息端地址对外暴露服务，实现消息接受及处理。目前提供三种类型的地址：`}</p>
    <h4 {...{
      "id": "gossip"
    }}>{`Gossip`}</h4>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`/gossip/[service_name]/[message_name]
`}</code></pre>
    <p>{`消息单向广播以及单播`}</p>
    <h4 {...{
      "id": "rpc-call"
    }}>{`RPC Call`}</h4>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`/rpc_call/[service_name]/[message_name]
`}</code></pre>
    <h4 {...{
      "id": "rpc-response"
    }}>{`RPC Response`}</h4>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`/rpc_resp/[service_name]/[message_name]
`}</code></pre>
    <p>{`RPC 用于节点之间的消息交互通信，RPC Call 发送请求，RPC Response 返回。`}</p>
    <h3 {...{
      "id": "消息序列化"
    }}>{`消息序列化`}</h3>
    <p>{`序列化采用 protobuf ，消息需要实现 MessageCodec trait 。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-rust"
      }}>{`#[async_trait]
pub trait MessageCodec: Sized + Send + Debug + 'static {
    async fn encode(&mut self) -> ProtocolResult<Bytes>;

    async fn decode(bytes: Bytes) -> ProtocolResult<Self>;
}
`}</code></pre>
    <p>{`目前针对实现了 serde Serialize 和 Deserialize trait 的消息自动实现了 MessageCodec ，
采用 bincode 作为中间序列化过渡。`}</p>
    <h3 {...{
      "id": "消息处理"
    }}>{`消息处理`}</h3>
    <p>{`消息处理需要实现 MessageHandler trait`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-rust"
      }}>{`#[async_trait]
pub trait MessageHandler: Sync + Send + 'static {
    type Message: MessageCodec;

    async fn process(&self, ctx: Context, msg: Self::Message) -> ProtocolResult<()>;
}
`}</code></pre>
    <h3 {...{
      "id": "消息处理逻辑注册"
    }}>{`消息处理逻辑注册`}</h3>
    <p>{`完成上述实现之后，可通过如下接口，完成消息逻辑处理的注册。`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-rust"
      }}>{`pub fn register_endpoint_handler<M>(
    &mut self,
    end: &str,
    handler: Box<dyn MessageHandler<Message = M>>,
) -> ProtocolResult<()>
where
    M: MessageCodec;

pub fn register_rpc_response<M>(&mut self, end: &str) -> ProtocolResult<()>
where
    M: MessageCodec;
`}</code></pre>
    <p><inlineCode parentName="p">{`Gossip`}</inlineCode>{` 和 `}<inlineCode parentName="p">{`RPC Call`}</inlineCode>{` 都需要通过 `}<inlineCode parentName="p">{`register_endpoint_handler`}</inlineCode>{` 完成注册，
而 `}<inlineCode parentName="p">{`RPC Response`}</inlineCode>{` 需要通过 `}<inlineCode parentName="p">{`register_rpc_response`}</inlineCode>{` 完成注册。`}</p>
    <p>{`未来计划将 `}<inlineCode parentName="p">{`RPC Response`}</inlineCode>{` 注册去掉。`}</p>
    <p><inlineCode parentName="p">{`end`}</inlineCode>{` 即签名提到的节点消息端 `}<inlineCode parentName="p">{`Endpoint`}</inlineCode>{` 缩写。`}</p>
    <h3 {...{
      "id": "消息的发送"
    }}>{`消息的发送`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-rust"
      }}>{`#[async_trait]
pub trait Gossip: Send + Sync {
    async fn broadcast<M>(&self, cx: Context, end: &str, msg: M, p: Priority) -> ProtocolResult<()>
    where
        M: MessageCodec;

    async fn users_cast<M>(
        &self,
        cx: Context,
        end: &str,
        users: Vec<UserAddress>,
        msg: M,
        p: Priority,
    ) -> ProtocolResult<()>
    where
        M: MessageCodec;
}

#[async_trait]
pub trait Rpc: Send + Sync {
    async fn call<M, R>(&self, ctx: Context, end: &str, msg: M, pri: Priority) -> ProtocolResult<R>
    where
        M: MessageCodec,
        R: MessageCodec;

    async fn response<M>(&self, cx: Context, end: &str, msg: M, p: Priority) -> ProtocolResult<()>
    where
        M: MessageCodec;
}
`}</code></pre>
    <p>{`如上述定义，网路服务实例化后，可通过调用 `}<inlineCode parentName="p">{`handle()`}</inlineCode>{` 获取一个网络服务引用，该
`}<inlineCode parentName="p">{`handle`}</inlineCode>{` 实现了上述的接口，同时实现了 `}<inlineCode parentName="p">{`Clone`}</inlineCode>{`。各模块可以通过它来完成消息的
发送。`}</p>
    <p>{`注意：`}<inlineCode parentName="p">{`UserAddress`}</inlineCode>{` 目前同 `}<inlineCode parentName="p">{`tentacle-secio`}</inlineCode>{` 提供的 secp256k1 公钥绑定。`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
