  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Genesis Configuration',
	description: 'The fundamental Muta concepts. A great place to start learning about Muta.'
};
export const metadata = {
  "id": "setup/genesis-config",
  "title": "Genesis Configuration",
  "description": "The fundamental Muta concepts. A great place to start learning about Muta.",
  "source": "@site/docs/setup/genesis-config.md",
  "permalink": "/new-docs/docs/setup/genesis-config",
  "editUrl": "https://github.com/nervosnetwork/muta/edit/master/website/docs/setup/genesis-config.md",
  "sidebar": "docs_zh",
  "previous": {
    "title": "Configuration",
    "permalink": "/new-docs/docs/setup/config"
  },
  "next": {
    "title": "Node Configuration",
    "permalink": "/new-docs/docs/setup/node-config"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Overview',
  id: 'overview',
  children: []
}, {
  value: 'Full list of blockchain parameters',
  id: 'full-list-of-blockchain-parameters',
  children: [{
    value: 'Initialization parameters',
    id: 'initialization-parameters',
    children: []
  }, {
    value: 'Asset service parameters',
    id: 'asset-service-parameters',
    children: []
  }, {
    value: 'Metadata service parameters',
    id: 'metadata-service-parameters',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`In this section we will understand how to customize genesis parameters in `}<inlineCode parentName="p">{`devtools/chain/genesis.toml`}</inlineCode>{` which affect the entire chain and network. `}</p>
    <h2 {...{
      "id": "overview"
    }}>{`Overview`}</h2>
    <p>{`Genesis parameters are global to a blockchain, and most of them are critical to that blockchain’s consensus. Once the blockchain is initialized, most of these parameters cannot be changed. However, a small number can be updated using the metadata service and are marked as Upgradable below. All the nodes in a blockchain network should share the same `}<inlineCode parentName="p">{`genesis.toml`}</inlineCode>{`.`}</p>
    <p>{`Let's take a look at `}<inlineCode parentName="p">{`devtools/chain/genesis.toml`}</inlineCode>{`.`}</p>
    <pre><code parentName="pre" {...{}}>{`timestamp = 0
prevhash = "0x44915be5b6c20b0678cf05fcddbbaa832e25d7e6ac538784cd5c24de00d47472"

[[services]]
name = "asset"
payload = '''
{
    "id": "0xf56924db538e77bb5951eb5ff0d02b88983c49c45eea30e8ae3e7234b311436c",
    "name": "MutaToken",
    "symbol": "MT",
    "supply": 320000011,
    "issuer": "0xf8389d774afdad8755ef8e629e5a154fddc6325a"
}
'''

[[services]]
name = "metadata"
payload = '''
{
    "chain_id": "0xb6a4d7da21443f5e816e8700eea87610e6d769657d6b8ec73028457bf2ca4036",
    "common_ref": "0x703873635a6b51513451",
    "timeout_gap": 20,
    "cycles_limit": 4294967295,
    "cycles_price": 1,
    "interval": 3000,
    "verifier_list": [
        {
            "bls_pub_key": "0x04188ef9488c19458a963cc57b567adde7db8f8b6bec392d5cb7b67b0abc1ed6cd966edc451f6ac2ef38079460eb965e890d1f576e4039a20467820237cda753f07a8b8febae1ec052190973a1bcf00690ea8fc0168b3fbbccd1c4e402eda5ef22",
            "address": "0xf8389d774afdad8755ef8e629e5a154fddc6325a",
            "propose_weight": 1,
            "vote_weight": 1
        }
    ],
    "propose_ratio": 15,
    "prevote_ratio": 10,
    "precommit_ratio": 10,
    "brake_ratio": 7,
    "tx_num_limit": 20000,
    "max_tx_size": 1024
}
'''
`}</code></pre>
    <p>{`Let’s go line-by-line and understand what each parameter means.`}</p>
    <h2 {...{
      "id": "full-list-of-blockchain-parameters"
    }}>{`Full list of blockchain parameters`}</h2>
    <h3 {...{
      "id": "initialization-parameters"
    }}>{`Initialization parameters`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Parameter`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Default`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`timestamp`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`timestamp of genesis block, can be set to arbitrary timestamp. For example, it can be 0 or 00:00 on some day.`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`0`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`prevhash`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`can be set to arbitrary value.`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`0x`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "asset-service-parameters"
    }}>{`Asset service parameters`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Parameter`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Default`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`id`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`unique identifier of an asset, it's recommended to set it as a hash value avoid duplication with other assets on the chain in the future`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`0`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`name`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`asset name`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`0x`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`symbol`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`asset short name`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`supply`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`total supply of the asset`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`issuer`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`issuer address`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "metadata-service-parameters"
    }}>{`Metadata service parameters`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Parameter`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`Default`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`chain_id`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`unique identifier of the chain, it's recommended to set it as a random hash value.`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`common_ref`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`needed for BLS signature`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`20`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`timeout_gap`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`max timeout an exchange pool can wait on a block. Users need to fill in `}<inlineCode parentName="td">{`timeout`}</inlineCode>{` when starting a transaction to indicate this transaction will not be included in the chain after block height exceeds this value. This will make sure the failure of the timeout transaction and avoid user starting a new transaction with new `}<inlineCode parentName="td">{`nonce`}</inlineCode>{` after waiting for a long time which produces 2 transactions on the chain eventually. If user entered `}<inlineCode parentName="td">{`timeout`}</inlineCode>{` > `}<inlineCode parentName="td">{`chain_current_height`}</inlineCode>{` + `}<inlineCode parentName="td">{`timeout_gap`}</inlineCode>{`, exchange pool will reject this transaction. For some special cases (Ex: cold wallet sends the transaction after signing for a long time), `}<inlineCode parentName="td">{`transaction_gap`}</inlineCode>{` can be set to a bigger value accordingly`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`cycles_limit`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`base 10 number, max `}<inlineCode parentName="td">{`cycle`}</inlineCode>{` can be run for a single transaction on the chain level`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1000000`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`cycles_price`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`minimum price for a cycle, currently not in use`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`interval`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`interval between generating blocks, measured in millisecond. When it's set to 3s, it's not strictly 3s but it's about 3s due to optimization of `}<inlineCode parentName="td">{`Overload`}</inlineCode>{` consensus on latency. It will be less than 3s in a good network condition, and more than 3s in a bad network condition`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`3000`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`verifier_list`}</strong></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`Validator List`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`bls_pub_key`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`BLS public key of the node`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`address`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`node address`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`propose_weight`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`weight of node when producing blocks. For example: 4 nodes, weights are `}<inlineCode parentName="td">{`1, 2, 3, 4`}</inlineCode>{`, first node's chance of producing a block is `}<inlineCode parentName="td">{`1 / (1 + 2 + 3 + 4)`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`vote_weight`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`weight of node when voting, similar calculation process as `}<inlineCode parentName="td">{`propose_weight`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`propose_ratio`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`ratio between timeout and block producing time in propose stage. For example: if `}<inlineCode parentName="td">{`propose_ratio`}</inlineCode>{` is 5, `}<inlineCode parentName="td">{`interval`}</inlineCode>{` is 3000ms, then timeout at propose stage is `}<inlineCode parentName="td">{`15 / 10 * 3000 = 4500`}</inlineCode>{`, all measured in millisecond`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`15`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`prevote_ratio`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`ratio between timeout and block producing time in prevote stage`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`10`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`precommit_ratio`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`ratio between timeout and block producing time in precommit stage`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`10`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`brake_ratio`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`ratio between timeout and block producing time in brake stage`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`7`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`tx_num_limit`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`max number of transaction in a single block`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`20000`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`max_tx_size`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`max number of byte in a single transaction`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1024`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
