  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	sidebar_label: 'Overview',
	title: 'Development Overview'
};
export const metadata = {
  "id": "dev/dev-overview",
  "title": "Development Overview",
  "description": "In this section, we'll show you how to customize the chain's functionality by developing a Service. Service is the abstraction layer in the Muta framework for extensions, where each Service performs a relatively independent function, maintaining its own storage and operational interfaces, similar to a Small state machine. These services together form the state machine part of the chain and after being connected to the underlying components of the blockchain through the framework interface, a brand new chain exclusive to you is developed!",
  "source": "@site/docs/dev/dev-overview.md",
  "permalink": "/new-docs/docs/dev/dev-overview",
  "editUrl": "https://github.com/nervosnetwork/muta/edit/master/website/docs/dev/dev-overview.md",
  "sidebar_label": "Overview",
  "sidebar": "docs",
  "previous": {
    "title": "Deployment",
    "permalink": "/new-docs/docs/setup/deploy"
  },
  "next": {
    "title": "Service Dev Guide",
    "permalink": "/new-docs/docs/dev/service-dev"
  }
};
  /* @jsx mdx */
import Step from '@site/src/components/Step';
import Jump from '@site/src/components/Jump';
export const rightToc = [{
  value: 'Learning Path',
  id: 'learning-path',
  children: [{
    value: 'Read the Service Development Guide',
    id: 'read-the-service-development-guide',
    children: []
  }, {
    value: 'Learn more about developing services with reference to existing services.',
    id: 'learn-more-about-developing-services-with-reference-to-existing-services',
    children: []
  }, {
    value: 'Try to develop a simple attestation service to build a POE chain.',
    id: 'try-to-develop-a-simple-attestation-service-to-build-a-poe-chain',
    children: []
  }, {
    value: 'Learn to develop more complex applications - decentralized exchanges',
    id: 'learn-to-develop-more-complex-applications---decentralized-exchanges',
    children: []
  }]
}, {
  value: 'Built-in Service Declaration',
  id: 'built-in-service-declaration',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`In this section, we'll show you how to customize the chain's functionality by developing a Service. Service is the abstraction layer in the Muta framework for extensions, where each Service performs a relatively independent function, maintaining its own storage and operational interfaces, similar to a Small state machine. These services together form the state machine part of the chain and after being connected to the underlying components of the blockchain through the framework interface, a brand new chain exclusive to you is developed!`}</p>
    <h2 {...{
      "id": "learning-path"
    }}>{`Learning Path`}</h2>
    <Step headingDepth={3} mdxType="Step">
      <ol>
        <li>
          <h3 {...{
            "id": "read-the-service-development-guide"
          }}>{`Read the Service Development Guide`}</h3>
          <p>{`This document describes service components and development guidelines.`}</p>
          <Jump to="../service-dev/" mdxType="Jump">View Service Dev Guide</Jump>
        </li>
        <li>
          <h3 {...{
            "id": "learn-more-about-developing-services-with-reference-to-existing-services"
          }}>{`Learn more about developing services with reference to existing services.`}</h3>
          <p>{`We provide a number of generic services for reference and use, covering governance, assets, multi-signature and more.`}</p>
          <Jump to="../../dev/service-list/" mdxType="Jump">View Service List</Jump>
        </li>
        <li>
          <h3 {...{
            "id": "try-to-develop-a-simple-attestation-service-to-build-a-poe-chain"
          }}>{`Try to develop a simple attestation service to build a POE chain.`}</h3>
          <p>{`In this tutorial, we will take you through the development of a simple certificate of deposit service from scratch and connect to Muta to build a POE chain.`}</p>
          <Jump to="../poe-chain/" mdxType="Jump">Develop A attestation Service</Jump>
        </li>
        <li>
          <h3 {...{
            "id": "learn-to-develop-more-complex-applications---decentralized-exchanges"
          }}>{`Learn to develop more complex applications - decentralized exchanges`}</h3>
          <Jump to="../dex/" mdxType="Jump">Build a DEX</Jump>
        </li>
      </ol>
    </Step>
    <h2 {...{
      "id": "built-in-service-declaration"
    }}>{`Built-in Service Declaration`}</h2>
    <p>{`The Muta framework source code currently has four built-in built-in services: Metadata Service, Asset Service, Authorization service and Multi-signature service. The configuration items related to the Metadata Service are necessary to start the chain. The other services are optional and can be configured in the Genesis block according to the developer's needs.`}</p>
    <Jump to="../service-list/" mdxType="Jump">Metadata Service</Jump>
    <Jump to="../service-list/" mdxType="Jump">Asset Service</Jump>
    <Jump to="../service-list/" mdxType="Jump">Authorization Service</Jump>
    <Jump to="../service-list/" mdxType="Jump">Multi-signature Service</Jump>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
