pub enum DexError {
    #[display(fmt = "Parsing payload to json failed {:?}", _0)]
    JsonParse(serde_json::Error),

    IllegalTrade,

    TradeExisted,

    TradeNotExisted,

    OrderOverdue,

    OrderNotExisted,
