    fn code(&self) -> u64 {
        match self {
            DexError::JsonParse(_) => 201,
            DexError::IllegalTrade { .. } => 202,
            DexError::TradeExisted { .. } => 203,
            DexError::TradeNotExisted { .. } => 204,
            DexError::OrderOverdue => 205,
            DexError::OrderNotExisted => 206,
        }
    }
}

